import { useState } from "react";
import React from "react";
import Banner from "./Banner";
import Formulario from "./Formulario";
import Time from "./Time";
export default function App() {
  const times = [
    {
      nome: "Programação",
      corPrimaria: "#57C278",
      corSecundaria: "",
    },
    {
      nome: "Front-End",
      corPrimaria: "#82CFFA",
      corSecundaria: "",
    },
    {
      nome: "Data Science",
      corPrimaria: "#A6D157",
      corSecundaria: "",
    },
    {
      nome: "Devops",
      corPrimaria: "#E06B69",
      corSecundaria: "",
    },
    {
      nome: "UX e Design",
      corPrimaria: "#DB6EBF",
      corSecundaria: "",
    },
    {
      nome: "Mobile",
      corPrimaria: "#FFBA05",
      corSecundaria: "",
    },
    {
      nome: "Inovação e Gestão",
      corPrimaria: "#FF8A29",
      corSecundaria: "",
    },
  ];

  const [colaboradores, setColaboradores] = useState([]);

  const aoNovoColaboradorCadastrado = (colaborador) => {
    console.log(colaborador);
    setColaboradores([...colaboradores, colaborador]);
  };

  return (
    <>
      <Banner />
      <Formulario
        aoColaboradorCadastrado={(colaborador) =>
          aoNovoColaboradorCadastrado(colaborador)
        }
      />

      <Time nome="Programação" cor="#57C278" colaboradores={colaboradores} />
      <Time nome="Front-End" cor="#82CFFA" colaboradores={colaboradores} />
      <Time nome="Data Science" cor="#A6D157" colaboradores={colaboradores} />
      <Time nome="Devops" cor="#E06B69" colaboradores={colaboradores} />
      <Time nome="UX e Design" cor="#DB6EBF" colaboradores={colaboradores} />
      <Time nome="Mobile" cor="#FFBA05" colaboradores={colaboradores} />
      <Time
        nome="Inovação e Gestão"
        cor="#FF8A29"
        colaboradores={colaboradores}
      />
    </>
  );
}
